const App = () => {
    const [ persons, setPersons ] = useState([
        { name: 'Arto Hellas'}
    ])

    const [ newName, setNewName ] = useState('')

    const rows = () => persons.map(person => <p>{person.name}</p>)

    return (
        <div>
            <h2>Phonebook</h2>
            <form>
                <div>
                    name: <input />
                </div>
                <div>
                    <button type="submit">add</button>
                </div>
            </form>
            <h2>Numbers</h2>
            {rows}
            
        </div>
        
    )
}

export default App
